import React, { useState } from "react";
import "../css/style.css";
import "bootstrap/dist/css/bootstrap.min.css";

var v_firstName = (firstName) => {
  if (firstName.length < 5) return false;
  return true;
};

export default function Register() {
  const [user, setUser] = useState({});
  const [errors, setErrors] = useState({});

  console.log(errors);
  console.log(user);

  return (
    <div className="container-form">
      <form>
        <div className="form-group">
          <label htmlFor="">First name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => {
              if (!v_firstName(e.target.value))
                setErrors({
                  firstName: "first name should ....",
                });
              setUser({
                ...user,
                firstName: e.target.value,
              });
            }}
            placeholder="First name"
            required
          />
          {errors.firstName && errors.firstName != "" && <div>salam</div>}
        </div>
        <div className="form-group">
          <label>Last name</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => {
              setUser({
                ...user,
                lastName: e.target.value,
              });
            }}
            placeholder="Last name"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="">Username</label>
          <input
            type="text"
            className="form-control"
            onChange={(e) => {
              setUser({
                ...user,
                username: e.target.value,
              });
            }}
            placeholder="username"
            required
          />
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            className="form-control"
            onChange={(e) => {
              setUser({
                ...user,
                email: e.target.value,
              });
            }}
            placeholder="Email"
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="">Password</label>
          <input
            type="password"
            className="form-control"
            onChange={(e) => {
              setUser({
                ...user,
                password: e.target.value,
              });
            }}
            placeholder="username"
            required
          />
        </div>
        <div className="form-group">
          <label>Repeat Password</label>
          <input
            type="password"
            className="form-control"
            onChange={(e) => {
              setUser({
                ...user,
                repeatPass: e.target.value,
              });
            }}
            placeholder="Password"
            required
          />
        </div>
        <button type="submit" className="btn-brown">
          Register
        </button>
      </form>
    </div>
  );
}
